import _ from "underscore";

const STORE_NAME_PROP = "namespace";

export default class StoreNames {
    constructor(storesInfo) {
        this._storesInfo = storesInfo;
    }

    primaryStorage() {
        return _.pluck(
            _.filter(this._storesInfo, (storeInfo) => {
                return  !storeInfo.isLocalStorage &&
                        !storeInfo.isNullStorage &&
                        !storeInfo.isFileStorage;
            }),
            STORE_NAME_PROP
        );
    }

    localStorage() {
        return _.pluck(
            _.where(this._storesInfo, { isLocalStorage: true }),
            STORE_NAME_PROP
        );
    }

    nullStorage() {
        return _.pluck(
            _.where(this._storesInfo, { isNullStorage: true }),
            STORE_NAME_PROP
        );
    }

    fileStorage() {
        return _.pluck(
            _.where(this._storesInfo, { isFileStorage: true }),
            STORE_NAME_PROP
        );
    }
}

