import { App } from "spak";
const REMOVED = "domain.storeValueRemoved";

export default function domainRemove(storeVal) {
    var parentId = storeVal.get("parentId");
    var details = storeVal.store.remove(storeVal.key);
    App.events.publish(REMOVED, {
        storeVal,
        id: storeVal.key,
        previous: details.value,
        parentId
    });
    return details;
}
