export default function getAllStoreit(target, local) {
    function getAllFilteredByProperty(propName, propValue) {
        var results = [];
        Object.keys(local.cache).forEach(function (key) {
            var value = local.getValue(key);
            if (!propName || value[propName] === propValue) {
                results.push(value);
            }
        });
        return results;
    }

    target.getAll = function () {
        return getAllFilteredByProperty();
    };

    target.getAllFilteredByProperty = getAllFilteredByProperty;
}
